INSERT INTO `bk_article` VALUES ('3', 'dedecms织梦内容管理系统简单介绍', '杨松', '&lt;h1&gt;&lt;a name=&quot;_Toc415239009&quot;&gt;&lt;/a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;一、基本概念：&lt;/span&gt;&lt;/h1&gt;&lt;h2&gt;&lt;a name=&quot;_Toc415239010&quot;&gt;&lt;/a&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;、&lt;/span&gt;cms&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;content management system&lt;span style=&quot;font-family:宋体&quot;&gt;）内容管理系统，&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;适合于管理什么系统（适合于做什么类型的网站）&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;）以静态为主，&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;2&lt;span style=&quot;font-family:宋体&quot;&gt;）以内容展示为主，&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;3&lt;span style=&quot;font-family:宋体&quot;&gt;）逻辑比较简单，网站的结构，比较清晰明了（主页，栏目页&lt;/span&gt;(&lt;span style=&quot;font-family:宋体&quot;&gt;列表页面&lt;/span&gt;)&lt;span style=&quot;font-family:宋体&quot;&gt;，内容详情页面）&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;比如：电影网站，软件下载网站，图片素材网站，简单的企业门户网站，学校网站。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;不适合于：&lt;/span&gt;SNS&lt;span style=&quot;font-family:宋体&quot;&gt;（个人设置），股票，复杂的电子商务网站，内容更新比较频繁的网站。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;h2&gt;&lt;a name=&quot;_Toc415239011&quot;&gt;&lt;/a&gt;2&lt;span style=&quot;font-family:宋体&quot;&gt;、&lt;/span&gt;cms&lt;span style=&quot;font-family:宋体&quot;&gt;系统的选择&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;）&lt;/span&gt;dedecms&lt;span style=&quot;font-family:宋体&quot;&gt;，织梦&lt;/span&gt;cms&lt;span style=&quot;font-family:宋体&quot;&gt;系统，以简单，实用，开源而闻名，适合于中小型网站，一些企业和学校也在使用。偏于套模板。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;2&lt;span style=&quot;font-family:宋体&quot;&gt;）&lt;/span&gt;phpcms&lt;span style=&quot;font-family:宋体&quot;&gt;：专业度要高于&lt;/span&gt;dedecms,&lt;span style=&quot;font-family:宋体&quot;&gt;适合于做大站，适合于做二次开发。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;3&lt;span style=&quot;font-family:宋体&quot;&gt;）帝国&lt;/span&gt;cms&lt;span style=&quot;font-family:宋体&quot;&gt;：功能一般。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;h2&gt;&lt;a name=&quot;_Toc415239012&quot;&gt;&lt;/a&gt;3&lt;span style=&quot;font-family:宋体&quot;&gt;、开源的&lt;/span&gt;cms&lt;span style=&quot;font-family:宋体&quot;&gt;系统提供了哪些功能？&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;）强大灵活的后台管理功能。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;2&lt;span style=&quot;font-family:宋体&quot;&gt;）全站静态化（前台），添加完成内容后，通过后台的按钮直接生成静态页面。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;3&lt;span style=&quot;font-family:宋体&quot;&gt;）强大的采集功能，采集别人网站的数据。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;4&lt;span style=&quot;font-family:宋体&quot;&gt;）强大的标签功能，便于在模板页面中取出数据，&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;5&lt;span style=&quot;font-family:宋体&quot;&gt;）采用&lt;/span&gt;mvc&lt;span style=&quot;font-family:宋体&quot;&gt;架构，便于扩展新的功能。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;h1&gt;&lt;a name=&quot;_Toc415239013&quot;&gt;&lt;/a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;二、安装&lt;/span&gt;dedecms&lt;/h1&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;h2&gt;&lt;a name=&quot;_Toc415239014&quot;&gt;&lt;/a&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;、下载&lt;/span&gt;cms&lt;span style=&quot;font-family:宋体&quot;&gt;的安装包&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;2&lt;span style=&quot;font-family:宋体&quot;&gt;、直接把安装包拷贝网站的根目录&lt;/span&gt;&lt;/p&gt;&lt;p&gt;3&lt;span style=&quot;font-family:宋体&quot;&gt;、直接在浏览器中访问&lt;/span&gt;&lt;/p&gt;&lt;p&gt;4&lt;span style=&quot;font-family:宋体&quot;&gt;、第一次访问时，提示安装。&lt;/span&gt;&lt;/p&gt;&lt;h1&gt;&lt;a name=&quot;_Toc415239021&quot;&gt;&lt;/a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;五、表之间的关系，&lt;/span&gt;&lt;/h1&gt;&lt;h2&gt;&lt;a name=&quot;_Toc415239022&quot;&gt;&lt;/a&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;、&lt;/span&gt;dede&lt;span style=&quot;font-family:宋体&quot;&gt;里面的内容模型的概念。&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;内容模型：存储数据的类型，类似于鱼和鱼类的概念，人和人类的概念。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;比如：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;office 2016(&lt;span style=&quot;font-family: 宋体&quot;&gt;具体的一个软件&lt;/span&gt;)&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;内容模型：软件模型&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;春天的故事（具体的一首歌）&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;内容模型：音乐&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;李世民（具体的一个人）&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;内容模型：人类&lt;/span&gt;&lt;/p&gt;&lt;p&gt;dede&lt;span style=&quot;font-family:宋体&quot;&gt;里面模型是存储到&lt;/span&gt;dede_channeltype&lt;span style=&quot;font-family:宋体&quot;&gt;表里面&lt;/span&gt;&lt;/p&gt;&lt;h2&gt;&lt;a name=&quot;_Toc415239023&quot;&gt;&lt;/a&gt;2&lt;span style=&quot;font-family:宋体&quot;&gt;、内容主表和附加表：&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;要存储的数据：&lt;/span&gt; &lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;电影数据&lt;/span&gt;:&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;名称&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;导演&lt;/span&gt;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;主演&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;剧情&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;浏览量&lt;/span&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;音乐数据&lt;/span&gt; &lt;span style=&quot;font-family:宋体&quot;&gt;：&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;作词&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;作曲&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;歌词&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;原唱&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;名称&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;浏览量&lt;/span&gt; &lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;图片数据：&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;名称&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;分辨率&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;图片的格式&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;图片的名称&lt;/span&gt;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;图片的大小&lt;/span&gt;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;浏览量&lt;/span&gt; &lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;软件数据：&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;名称&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;语言&lt;/span&gt;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;作者&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;大小&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;格式&lt;/span&gt; &lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;商品数据：&lt;/span&gt; &lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;思考：如何设置表，用于存储以上数据。&lt;/span&gt; &lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;采用：分表（水平分割，垂直分割）技术，&lt;/span&gt; &lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;内容主表：用于存储公共的一些信息的，&lt;/span&gt;dede&lt;span style=&quot;font-family:宋体&quot;&gt;里面是&lt;/span&gt;dede_archives&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;附加表：用于存储数据的独有的信息，附加表是有多个的。&lt;/span&gt; &lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;比如当前：&lt;/span&gt; &lt;/p&gt;&lt;p&gt;dede_addonimage:&lt;span style=&quot;font-family:宋体&quot;&gt;图片的独特数据&lt;/span&gt; &lt;/p&gt;&lt;p&gt;dede_addonmusic:&lt;span style=&quot;font-family:宋体&quot;&gt;音乐的独特的数据&lt;/span&gt; &lt;/p&gt;&lt;p&gt;dede_addonmovie:&lt;span style=&quot;font-family:宋体&quot;&gt;电影数据&lt;/span&gt; &lt;/p&gt;&lt;h2&gt;&lt;a name=&quot;_Toc415239030&quot;&gt;&lt;/a&gt;2&lt;span style=&quot;font-family:宋体&quot;&gt;、自定义模型&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;当默认的模型无法满足我们时，要自己定义模型，（新建模型）&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;如何定义：&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;定义模型：新增一张附加表，定义附加表里面的字段信息。&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;字段信息如何定义：根据&lt;span style=&quot;color:red&quot;&gt;内容详情页面里面，显示的数据信息&lt;/span&gt;而定义，&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;要注意：有些信息无需定义，因为在内容主表里面已经定义了，比如名称，缩略图的地址等。&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;h2&gt;&lt;a name=&quot;_Toc415239032&quot;&gt;&lt;/a&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;、模板替换，&lt;/span&gt;&lt;/h2&gt;&lt;h2&gt;&lt;a name=&quot;_Toc415239033&quot;&gt;&lt;/a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;）在&lt;/span&gt;dede&lt;span style=&quot;font-family:宋体&quot;&gt;目录下面的&lt;/span&gt;templets&lt;span style=&quot;font-family:宋体&quot;&gt;目录下面存储是模板文件。&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;在&lt;/span&gt;templets&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;目录下面新建一个目录，为&lt;/span&gt;a67,&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;用于存储我们电影网站的模板文件。&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;h2&gt;&lt;a name=&quot;_Toc415239036&quot;&gt;&lt;/a&gt;2&lt;span style=&quot;font-family:宋体&quot;&gt;、使用标签取出数据&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;在&lt;/span&gt;dede&lt;span style=&quot;font-family:宋体&quot;&gt;里面有两种标签，单标签和双标签。&lt;strong&gt;标签只能在模板文件中使用&lt;/strong&gt;。&lt;/span&gt;&lt;/p&gt;&lt;h3&gt;&lt;a name=&quot;_Toc415239037&quot;&gt;&lt;/a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;）单标签：&lt;/span&gt;&lt;/h3&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;使用语法：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;{dede:global.&lt;span style=&quot;font-family: 宋体&quot;&gt;标签的名称&lt;/span&gt;/}&lt;/p&gt;&lt;p&gt;{dede:global.cfg_webname/}&lt;/p&gt;&lt;p&gt;{dede:global.cfg_templets_skin/}&lt;span style=&quot;font-family:宋体&quot;&gt;，给我们解析成当前的模板目录：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;单标签是实际上是解析成全局变量，在&lt;/span&gt;include&lt;span style=&quot;font-family:宋体&quot;&gt;目录下面有一个文件&lt;/span&gt;&lt;strong&gt;common.inc.php&lt;/strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;，该文件里面定义了一些全局变量，&lt;strong&gt;如果想在模板文件中使用全局变量，则使用单标签来取出&lt;/strong&gt;。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;自己可以建立一个&lt;strong&gt;测试文件&lt;/strong&gt;，用于测试标签内容的解析，比如在&lt;/span&gt;a67&lt;span style=&quot;font-family:宋体&quot;&gt;下面建立一个&lt;/span&gt;demo.htm&lt;span style=&quot;font-family:宋体&quot;&gt;文件，用于测试，&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;语法格式：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;{dede:&lt;span style=&quot;font-family:宋体&quot;&gt;标签的名称&lt;/span&gt;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;属性&lt;/span&gt;1=&lt;span style=&quot;font-family:宋体&quot;&gt;属性值&lt;/span&gt;1&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;属性&lt;/span&gt;2=&lt;span style=&quot;font-family:宋体&quot;&gt;属性值&lt;/span&gt;2}&lt;/p&gt;&lt;p style=&quot;margin-left:28px;text-indent:28px&quot;&gt;[field:&lt;span style=&quot;font-family:宋体&quot;&gt;字段名称&lt;/span&gt;/]&lt;/p&gt;&lt;p&gt;{/dede:&lt;span style=&quot;font-family: 宋体&quot;&gt;标签的名称&lt;/span&gt;}&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;table cellpadding=&quot;0&quot; cellspacing=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr&gt;&lt;td width=&quot;436&quot; height=&quot;5&quot;&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;br/&gt;&lt;/td&gt;&lt;td width=&quot;222&quot; height=&quot;55&quot; bgcolor=&quot;white&quot; style=&quot;border:1px solid black;vertical-align:top;background:white&quot;&gt;&lt;span style=&quot;position:absolute;left:0;z-index:251687936&quot;&gt; &amp;nbsp; 标签对应表里面的字段名称&lt;/span&gt;&amp;nbsp;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br clear=&quot;ALL&quot;/&gt; &lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;案例&lt;/span&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;：取出栏目数据内容，使用的标签是，&lt;/span&gt;channel&lt;span style=&quot;font-family:宋体&quot;&gt;标签，&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;该标签在解析的时候，是查询的&lt;/span&gt;dede_arctype&lt;span style=&quot;font-family:宋体&quot;&gt;表&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;channeled=’&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;模型的&lt;/span&gt;id’:&lt;/strong&gt;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;通过该属性，用于确定是取出哪个模型的数据，也就是确定读取哪张附加表，如果没有该属性，则不读取附加表里面的数据。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;addfields=”&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;附加表字段多个用逗号隔开&lt;/span&gt;”&lt;/strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;，用于确定从附加表里面取出哪些字段信息。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 'dedecms，织梦cms系统，以简单，实用，开源而闻名，适合于中小型网站', 'dedecms，织梦cms系统，以简单，实用，开源而闻名，适合于中小型网站，一些企业和学校也在使用。偏于套模板。', 'Article/565043f3e7e13.png', '17', '127.0.0.1', '1503343437', '0', '33', '0', '1', '568', '1');
INSERT INTO `bk_article` VALUES ('4', 'mongodb的简介，非关系型数据库管理安装', '杨松', '&lt;h1&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;一、&lt;/span&gt;mongodb&lt;span style=&quot;font-family:宋体&quot;&gt;的简介&lt;/span&gt;&lt;/h1&gt;&lt;p&gt;MongoDB &lt;span style=&quot;font-family: 宋体&quot;&gt;是一个介于关系数据库和非关系数据库之间的产品，是非关系数据库当中功能最丰富，最像关系数据库的，语法有点类似&lt;/span&gt;javascript&lt;span style=&quot;font-family:宋体&quot;&gt;面向对象的查询语言，它是一个面向集合的，模式自由的文档型数据库。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;h1&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;二、掌握几个概念&lt;/span&gt;&lt;/h1&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;h2&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;、文档。&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;文档是&lt;/span&gt;mongoDB&lt;span style=&quot;font-family:宋体&quot;&gt;中数据的基本单元，类似关系数据库的行，&lt;/span&gt; &lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;多个键值对有序地放置在一起便是文档&lt;/span&gt;&lt;/p&gt;&lt;p&gt;{name:’xiaogang’,age:12,email:’nihao@sohu.com’}&lt;/p&gt;&lt;p&gt;{name:’xiowanzi’,age:11}&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;MongoDB &lt;span style=&quot;font-family: 宋体&quot;&gt;中以文档的方式存取记录，如一条记录格式如下：&lt;/span&gt; &lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; { “username”:”Tom”, “age”:10 }&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; { &amp;quot;username&amp;quot;:&amp;quot;Tom&amp;quot; , &amp;quot;age&amp;quot; : &amp;quot;10&amp;quot; }&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; {“Username”:”Tom”,”age”:10}&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;注意：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;1.&lt;span style=&quot;font-family:宋体&quot;&gt;以上是两个不同的文档，&lt;/span&gt;MongoDB&lt;span style=&quot;font-family:宋体&quot;&gt;区分大小写的数据类型，第一个&lt;/span&gt;age&lt;span style=&quot;font-family:宋体&quot;&gt;字段是&lt;strong&gt;数字&lt;/strong&gt;类型，第二个&lt;/span&gt;age&lt;span style=&quot;font-family:宋体&quot;&gt;是&lt;strong&gt;字符串&lt;/strong&gt;类型。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;2. &lt;span style=&quot;font-family:宋体&quot;&gt;每一个文档尺寸不能超过&lt;/span&gt;&lt;strong&gt;16M&lt;/strong&gt; &lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;h2&gt;2&lt;span style=&quot;font-family:宋体&quot;&gt;、集合，&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;多个文档组成一个集合，就类似于关系型数据库里面的表。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;集合就是一组文档，集合类似于表&lt;/span&gt; &lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;无模式是指，在&lt;strong&gt;同一个集合&lt;/strong&gt;中可以包含&lt;strong&gt;不同格式的文档&lt;/strong&gt;，如：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;{&amp;nbsp;&amp;nbsp; &amp;quot;Name&amp;quot; : &amp;quot;Mongodb&amp;quot; ,&amp;nbsp; &amp;quot;Type&amp;quot; : &amp;quot;Nosql&amp;quot;&amp;nbsp; }&lt;/p&gt;&lt;p&gt;{&amp;nbsp;&amp;nbsp; &amp;quot;UserName&amp;quot; : &amp;quot;Tom&amp;quot;&amp;nbsp; ,&amp;nbsp; &amp;quot;age&amp;quot;&amp;nbsp; :&amp;nbsp; 20&amp;nbsp; ,&amp;nbsp; &amp;quot;Gender&amp;quot;&amp;nbsp; :&amp;nbsp; &amp;quot;male&amp;quot;&amp;nbsp; }&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;以上两个文档可以放在同一个集合中。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;在&lt;/span&gt;Mysql&lt;span style=&quot;font-family:宋体&quot;&gt;需要先建表再插入数据，&lt;/span&gt; &lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;模式自由（&lt;/span&gt;schema-free&lt;span style=&quot;font-family:宋体&quot;&gt;）：意思是集合里面没有行和列的概念，&lt;/span&gt; &lt;/p&gt;&lt;p&gt;MongoDB&lt;span style=&quot;font-family: 宋体&quot;&gt;中的&lt;strong&gt;集合不用创建&lt;/strong&gt;、&lt;strong&gt;没有结构&lt;/strong&gt;，所以可以放不同格式的文档。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;h2&gt;3&lt;span style=&quot;font-family:宋体&quot;&gt;、数据库&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;多个集合组成一个数据库。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;多个集合可以组成数据库。一个&lt;/span&gt;mongoDB&lt;span style=&quot;font-family:宋体&quot;&gt;实例可以承载多个数据库，他们之间完全独立。&lt;/span&gt; &lt;/p&gt;&lt;p&gt;Mongodb&lt;span style=&quot;font-family: 宋体&quot;&gt;中的数据库和&lt;/span&gt;Mysql&lt;span style=&quot;font-family:宋体&quot;&gt;中的数据为概念类似，只是&lt;strong&gt;无需创建&lt;/strong&gt;。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;一个数据库中可以有多个集合。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;一个集合中可以有多个文档。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;3&lt;span style=&quot;font-family:宋体&quot;&gt;、以&lt;strong&gt;管理员&lt;/strong&gt;的方式进入&lt;/span&gt;cmd&lt;span style=&quot;font-family:宋体&quot;&gt;，进入到&lt;/span&gt;mongodb&lt;span style=&quot;font-family:宋体&quot;&gt;的&lt;/span&gt;bin&lt;span style=&quot;font-family:宋体&quot;&gt;目录里面。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;安装方式一：安装成&lt;/span&gt;window&lt;span style=&quot;font-family:宋体&quot;&gt;的一个服务。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;Mongod.exe &lt;strong&gt;--install&lt;/strong&gt; --dbpath &lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据目录&lt;/span&gt;&lt;/strong&gt; --logpath &lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;日志文件&lt;/span&gt; &lt;/strong&gt;&lt;/p&gt;&lt;p&gt;4&lt;span style=&quot;font-family:宋体&quot;&gt;、客户端连接&lt;/span&gt;mongodb&lt;span style=&quot;font-family:宋体&quot;&gt;服务器。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;使用&lt;/span&gt;mongo&lt;span style=&quot;font-family:宋体&quot;&gt;命令来连接服务器。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;默认连接本机的&lt;/span&gt;27017&lt;span style=&quot;font-family:宋体&quot;&gt;，如果要连接其他服务器，可以执行：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;语法：&lt;/span&gt; &lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; mongo.exe&amp;nbsp; &lt;strong&gt;ip&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 宋体&quot;&gt;地址&lt;/span&gt;&lt;/strong&gt;:&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;端口号&lt;/span&gt;&lt;/strong&gt;/&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据库名&lt;/span&gt;&lt;/strong&gt;&amp;nbsp; (&lt;span style=&quot;font-family:宋体&quot;&gt;默认连接&lt;/span&gt;test&lt;span style=&quot;font-family:宋体&quot;&gt;数据库&lt;/span&gt;)&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;如：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; mongo.exe localhost:27017/abc --&amp;gt;&lt;span style=&quot;font-family:宋体&quot;&gt;连接本地&lt;/span&gt;27017&lt;span style=&quot;font-family:宋体&quot;&gt;端口，&lt;/span&gt;abc&lt;span style=&quot;font-family:宋体&quot;&gt;数据库&lt;/span&gt; &lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;注：可以通过&lt;/span&gt; mongo.exe –help &lt;span style=&quot;font-family:宋体&quot;&gt;查看更多命令&lt;/span&gt; &lt;/p&gt;&lt;h1&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;五、入门语句&lt;/span&gt;&lt;/h1&gt;&lt;p&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;、创建数据库，&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;语法：&lt;/span&gt;&amp;nbsp; use&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;数据库名称&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;如果数据库存在，则是切换，如果不存在则是创建。如果创建了数据库，则没有任何操作，则会自动删除。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;2&lt;span style=&quot;font-family:宋体&quot;&gt;、&lt;/span&gt;show dbs&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;查看数据库。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;3&lt;span style=&quot;font-family:宋体&quot;&gt;、&lt;/span&gt;db&lt;span style=&quot;font-family:宋体&quot;&gt;命令表示当前数据库，直接输入&lt;/span&gt; db&lt;span style=&quot;font-family:宋体&quot;&gt;则返回所在数据库的名字。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;4&lt;span style=&quot;font-family:宋体&quot;&gt;、建立集合&lt;/span&gt;&lt;/p&gt;&lt;p&gt;db.&lt;span style=&quot;font-family:宋体&quot;&gt;集合名&lt;/span&gt;.insert({name:’xiao’})&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;5&lt;span style=&quot;font-family:宋体&quot;&gt;、&lt;/span&gt;show tables &lt;span style=&quot;font-family:宋体&quot;&gt;查看当前数据库下面的集合&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;6&lt;span style=&quot;font-family:宋体&quot;&gt;、帮助命令&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;在数据库级别查看帮助命令&lt;/span&gt;&lt;/p&gt;&lt;p&gt;db.help()&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;在集合级别下面查看帮助命令&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;在全局下面查看帮助命令&lt;/span&gt;&lt;/p&gt;&lt;h1&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;七、&lt;/span&gt;mongodb&lt;span style=&quot;font-family:宋体&quot;&gt;中权限验证&lt;/span&gt;&lt;/h1&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;mongodb&lt;span style=&quot;font-family: 宋体&quot;&gt;里面的用户名和密码是属于数据库的。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;h2&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;、创建用户&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;,&lt;span style=&quot;font-family:宋体&quot;&gt;分两步，（&lt;/span&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;）选择数据库（&lt;/span&gt;2&lt;span style=&quot;font-family:宋体&quot;&gt;）具体的创建用户&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;语法：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;user &lt;span style=&quot;font-family:宋体&quot;&gt;数据库的名称&lt;/span&gt;&lt;/p&gt;&lt;p&gt;db.addUser(‘&lt;span style=&quot;font-family: 宋体&quot;&gt;用户名&lt;/span&gt;’,’&lt;span style=&quot;font-family:宋体&quot;&gt;密码&lt;/span&gt;’,&lt;span style=&quot;font-family:宋体&quot;&gt;是否只读&lt;/span&gt;)&lt;span style=&quot;font-family:宋体&quot;&gt;如果第三个参数为&lt;/span&gt;ture&lt;span style=&quot;font-family:宋体&quot;&gt;则只能查询不能修改，默认是&lt;/span&gt;false&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;注意：在给数据库创建普通用户名之前，一定要创建一个超级管理员。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;案例&lt;/span&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;创建超级管理员：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;2&lt;span style=&quot;font-family:宋体&quot;&gt;、数据库用户验证，&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;要使用用户名验证，要开启数据库的权限验证。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;如何开启：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;在开始启动&lt;/span&gt;mongdb&lt;span style=&quot;font-family:宋体&quot;&gt;时，执行，&lt;/span&gt;&lt;/p&gt;&lt;p&gt;mongod.exe&amp;nbsp;&amp;nbsp; --dbpath&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;数据库文件&lt;/span&gt;&amp;nbsp; --logpath &lt;span style=&quot;font-family: 宋体&quot;&gt;日志文件路径&lt;/span&gt;&amp;nbsp; &lt;strong&gt;&lt;span style=&quot;color:red&quot;&gt;--auth&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;color:red&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体;color:#0D0D0D&quot;&gt;如果是以&lt;/span&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;window&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体;color:#0D0D0D&quot;&gt;服务安装的，则要卸载服务重新安装，在安装命令后面添加&lt;/span&gt;&lt;span style=&quot;color:red&quot;&gt; --auth&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体;color:#0D0D0D&quot;&gt;如何卸载&lt;/span&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;mon godb&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体;color:#0D0D0D&quot;&gt;的服务：&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体;color:#0D0D0D&quot;&gt;先停止服务，进入到&lt;/span&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;cmd&amp;nbsp; bin&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体;color:#0D0D0D&quot;&gt;目录里面，执行&lt;/span&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;mongod&amp;nbsp; --remove&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体;color:#0D0D0D&quot;&gt;即可&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;4&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体;color:#0D0D0D&quot;&gt;、用户的管理。&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体;color:#0D0D0D&quot;&gt;删除用户，或重新设置密码&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体;color:#0D0D0D&quot;&gt;（&lt;/span&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;1&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体;color:#0D0D0D&quot;&gt;）删除用户：可以在&lt;/span&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;system.users&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体;color:#0D0D0D&quot;&gt;集合里面直接把该文档给删除。&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;db.system.users.find()&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;db.&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;color:red&quot;&gt;system.users&lt;/span&gt;&lt;strong&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;.remove({“user”:”test”})&amp;nbsp; &lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:Wingdings;color:#0D0D0D&quot;&gt;à&lt;/span&gt; &lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体;color:#0D0D0D&quot;&gt;删除&lt;/span&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;test &lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体;color:#0D0D0D&quot;&gt;账号&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体;color:#0D0D0D&quot;&gt;修改：&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;2&lt;span style=&quot;font-family:宋体&quot;&gt;）修改用户密码&lt;/span&gt;&lt;/p&gt;&lt;p&gt;use test&lt;/p&gt;&lt;p&gt;db.changeUserPassword(&lt;span style=&quot;font-family:宋体&quot;&gt;用户名&lt;/span&gt;,&lt;span style=&quot;font-family:宋体&quot;&gt;新密码&lt;/span&gt;)&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;h1&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;八、&lt;/span&gt;php&lt;span style=&quot;font-family:宋体&quot;&gt;支持&lt;/span&gt;mongodb&lt;/h1&gt;&lt;h2&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;、安装扩展，准备扩展文件&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;安装扩展，要注意支持的&lt;/span&gt;php&lt;span style=&quot;font-family:宋体&quot;&gt;版本，&lt;/span&gt;vc&lt;span style=&quot;font-family:宋体&quot;&gt;几编译的，&lt;/span&gt;nts&lt;span style=&quot;font-family:宋体&quot;&gt;或&lt;/span&gt;ts&lt;span style=&quot;font-family:宋体&quot;&gt;的&lt;/span&gt;&lt;/p&gt;&lt;h2&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;、连接&lt;/span&gt;mongodb&lt;span style=&quot;font-family:宋体&quot;&gt;服务器，&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;//MongoDB&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体;color:#0D0D0D&quot;&gt;有用户名密码并指定数据库&lt;/span&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;blog &lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;//$conn = new Mongo(&amp;quot;mongodb://${username}:${password}@localhost/blog&amp;quot;); &lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;//$m=new Mongo(&amp;quot;mongodb://hanhao:123456@localhost:1111/php&amp;quot;); &lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;$m=new Mongo(&amp;quot;mongodb://root:1234@localhost:1111/admin&amp;quot;); &lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;$db=$m-&amp;gt;selectDb(&amp;quot;php&amp;quot;); &lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;$data = $db-&amp;gt;user-&amp;gt;find(array(&amp;#39;age&amp;#39;=&amp;gt;array(&amp;#39;$gt&amp;#39;=&amp;gt;2))); &lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;print_r($data); &lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;foreach($data as $v){ &lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; echo &amp;#39;name:&amp;#39;.$v[&amp;#39;name&amp;#39;].&amp;#39;---&amp;#39;.$v[&amp;#39;age&amp;#39;].&amp;#39;&amp;lt;br/&amp;gt;&amp;#39;;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;h2&gt;2&lt;span style=&quot;font-family:宋体&quot;&gt;、选择数据库&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;$db=$m-&amp;gt;selectDb(&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体;color:#0D0D0D&quot;&gt;数据库的名称&lt;/span&gt;&lt;span style=&quot;color:#0D0D0D&quot;&gt;);&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体;color:#0D0D0D&quot;&gt;具体的操作&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 'MongoDB 是一个介于关系数据库和非关系数据库之间的产品', 'MongoDB 是一个介于关系数据库和非关系数据库之间的产品，是非关系数据库当中功能最丰富，最像关系数据库的，语法有点类似javascript面向对象的查询语言，它是一个面向集合的，模式自由的文档型数据库。', 'Article/565045240e5f3.png', '20', '127.0.0.1', '1518101156', '0', '76', '0', '1', '1267', '1');
INSERT INTO `bk_article` VALUES ('5', 'sphinx的基本介绍，全文检索sql优化', '杨松', '&lt;h1&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;一、&lt;/span&gt;sphinx&lt;span style=&quot;font-family:宋体&quot;&gt;的基本介绍&lt;/span&gt;&lt;/h1&gt;&lt;h2&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;、使用背景&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;当对&lt;strong&gt;大文本&lt;/strong&gt;数据进行单词匹配&lt;strong&gt;查询&lt;/strong&gt;时，如：有一个歌曲网站，数据库中&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;收集着上百万首歌曲的信息，&lt;strong&gt;如果要查询出歌词中带“爱情”的所有歌&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;曲&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;，典型的做法是执行如下&lt;/span&gt;SQL&lt;span style=&quot;font-family:宋体&quot;&gt;语句：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;SELECT * FROM songs WHERE content LIKE &amp;quot;%&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;爱情&lt;/span&gt;%&amp;quot;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;。&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;但是在&lt;/span&gt;MYSQL&lt;span style=&quot;font-family:宋体&quot;&gt;中&lt;strong&gt;以&lt;/strong&gt;&lt;/span&gt;&lt;strong&gt;%&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;开头&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;的查询无法使用索引，所以这条&lt;/span&gt;SQL&lt;span style=&quot;font-family:宋体&quot;&gt;语句将执&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;行&lt;strong&gt;全表扫描&lt;/strong&gt;，性能&lt;strong&gt;极差&lt;/strong&gt;。&lt;/span&gt;&lt;strong&gt;MyISAM&lt;/strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;引擎中的&lt;strong&gt;全文索引&lt;/strong&gt;是专门对文本创建&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;索引的，但对&lt;strong&gt;中文&lt;/strong&gt;的&lt;strong&gt;支持不好&lt;/strong&gt;。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;好的解决方案：使用&lt;/span&gt;&lt;strong&gt;sphinx &lt;/strong&gt;!&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;h2&gt;2&lt;span style=&quot;font-family:宋体&quot;&gt;、&lt;/span&gt;sphinx&lt;span style=&quot;font-family:宋体&quot;&gt;的原理&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;第一步：对应数据源建立索引&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br clear=&quot;ALL&quot;/&gt; &lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;中文分词技术，&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;今天&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 1&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;下雨&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 1&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;明天&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 1,2,3&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;学习&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 2&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;第二步：执行查询，返回查询到单词所在&lt;/span&gt;,mysql&lt;span style=&quot;font-family:宋体&quot;&gt;里面记录的&lt;/span&gt;id&lt;/p&gt;&lt;p&gt;php&lt;span style=&quot;font-family:宋体&quot;&gt;要把查询的单词给&lt;/span&gt;sphinx&lt;span style=&quot;font-family:宋体&quot;&gt;软件，软件拿到要查询的单词后，从索引里面进行匹配。返回该单词在&lt;/span&gt;mysql&lt;span style=&quot;font-family:宋体&quot;&gt;表里面记录的&lt;/span&gt;id&lt;/p&gt;&lt;p&gt;php&lt;span style=&quot;font-family:宋体&quot;&gt;拿到单词所在的额&lt;/span&gt;id&lt;span style=&quot;font-family:宋体&quot;&gt;后，根据该&lt;/span&gt;id&lt;span style=&quot;font-family:宋体&quot;&gt;去&lt;/span&gt;mysql&lt;span style=&quot;font-family:宋体&quot;&gt;里面查找数据。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;h2&gt;3&lt;span style=&quot;font-family:宋体&quot;&gt;、什么是&lt;/span&gt;Coreseek&lt;/h2&gt;&lt;p&gt;Coreseek &lt;span style=&quot;font-family: 宋体&quot;&gt;是一款&lt;strong&gt;中文全文检索&lt;/strong&gt;&lt;/span&gt;/&lt;span style=&quot;font-family:宋体&quot;&gt;搜索软件&lt;/span&gt;,&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;基于&lt;/span&gt;Sphinx&lt;/strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;研发并独立发布，&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;专攻&lt;strong&gt;中文搜索&lt;/strong&gt;和信息处理领域，适用于行业&lt;/span&gt;/&lt;span style=&quot;font-family:宋体&quot;&gt;垂直搜索、论坛&lt;/span&gt;/&lt;span style=&quot;font-family:宋体&quot;&gt;站内搜索&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据库搜索、文档&lt;/span&gt;/&lt;span style=&quot;font-family:宋体&quot;&gt;文献检索、信息检索、数据挖掘等应用场景&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;h1&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;二、开始安装执行&lt;/span&gt;&lt;/h1&gt;&lt;p&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;、下载软件，解压后，拷贝到指定的目录&lt;/span&gt;&lt;/p&gt;&lt;p&gt;3&lt;span style=&quot;font-family:宋体&quot;&gt;、建立索引&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;配置数据源&lt;/span&gt;&amp;nbsp;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;配置索引&lt;/span&gt;&amp;nbsp; &lt;span style=&quot;font-family:宋体&quot;&gt;配置服务器端的信息。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;）配置数据源，&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;语法：&lt;/span&gt;source &amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-family:宋体&quot;&gt;数据源的名字&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;在一个配置文件中，可以配置多个数据源的。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;2&lt;span style=&quot;font-family:宋体&quot;&gt;）针对数据源配置索引，&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;一个数据源对应一个索引的配置，&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;语法：&lt;/span&gt;index &lt;span style=&quot;font-family:宋体&quot;&gt;索引的名字&lt;/span&gt;&lt;/p&gt;&lt;p&gt;4&lt;span style=&quot;font-family:宋体&quot;&gt;、根据&lt;/span&gt;sphinx.conf&lt;span style=&quot;font-family:宋体&quot;&gt;的配置，创建索引&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;以管理员进入&lt;/span&gt;cmd &lt;span style=&quot;font-family:宋体&quot;&gt;进入到&lt;/span&gt;sphinx/bin&lt;span style=&quot;font-family:宋体&quot;&gt;目录&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;5&lt;span style=&quot;font-family:宋体&quot;&gt;、安装&lt;/span&gt;sphinx(coreseek)&lt;span style=&quot;font-family:宋体&quot;&gt;启动服务&lt;/span&gt;,&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;以管理员进入&lt;/span&gt;cmd&lt;span style=&quot;font-family:宋体&quot;&gt;进入到&lt;/span&gt;sphinx/bin&lt;span style=&quot;font-family:宋体&quot;&gt;目录，使用&lt;/span&gt;searchd.exe&lt;span style=&quot;font-family:宋体&quot;&gt;命令&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;总结：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;）针对数据源建立索引，要配置的是&lt;strong&gt;数据源&lt;/strong&gt;，&lt;strong&gt;数据源对应的索引&lt;/strong&gt;，&lt;strong&gt;服务器端配置&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp; &lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;使用&lt;/span&gt;&lt;span style=&quot;color:red&quot;&gt;indexer&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;命令完成索引的创建。&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;2&lt;span style=&quot;font-family:宋体&quot;&gt;）启动&lt;/span&gt;sphinx&lt;span style=&quot;font-family:宋体&quot;&gt;的服务进行查询&lt;/span&gt;&lt;/p&gt;&lt;h1&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;四、查询的关键词要添加样式显示&lt;/span&gt;&lt;/h1&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;使用：&lt;/span&gt;$sc-&amp;gt;buildExcerpts()&lt;span style=&quot;font-family:宋体&quot;&gt;来完成对应关键词添加样式来显示&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;$sc-&amp;gt;buildExcerpts(&lt;span style=&quot;font-family:宋体&quot;&gt;一条记录的数组，索引的名字，查询的关键词&lt;/span&gt;,&lt;span style=&quot;font-family:宋体&quot;&gt;关键词添加的样式&lt;/span&gt;)&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;$sc-&amp;gt;buildExcerpts(&lt;span style=&quot;font-family:宋体&quot;&gt;一条记录的数组，索引的名字，查询的关键词&lt;/span&gt;,array(&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; ‘before_match’=&amp;gt;’&amp;lt;font color=”red”/&amp;gt;’,&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; ‘after_match’=&amp;gt;’&amp;lt;/font&amp;gt;’&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;))&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;该函数返回的是一个索引的数组，因此在输出时要使用下标来显示&lt;/span&gt;&lt;/p&gt;&lt;h1&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;五、&lt;/span&gt;SPHINX&lt;span style=&quot;font-family:宋体&quot;&gt;的匹配模型&lt;/span&gt;&lt;/h1&gt;&lt;h2&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;、&lt;/span&gt;SPH_MATCH_ALL &lt;span style=&quot;font-family:宋体&quot;&gt;完全匹配所有的词&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;如“冬天&lt;/span&gt;&amp;nbsp; &lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;的&lt;/span&gt;&amp;nbsp;&amp;nbsp; &lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;雪”，并不会匹配&lt;/span&gt; “&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;我爱冬天&lt;/span&gt;”,&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;但可以匹配&lt;/span&gt; “&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;我的朋友，爱冬天，和雪&lt;/span&gt;”&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;。&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;因为“冬天的雪”&lt;/span&gt; &lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;被分成&lt;/span&gt; &lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;“冬天”，“的”，“雪”三个词，匹配条件是同时包含&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;这三个词，“我爱冬天”里只包含一个“冬天”&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;h2&gt;2&lt;span style=&quot;font-family:宋体&quot;&gt;、&lt;/span&gt;SPH_MATCH_ANY: &lt;span style=&quot;font-family:宋体&quot;&gt;匹配任意一个词&lt;/span&gt;&lt;/h2&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;如“冬天&lt;/span&gt;&amp;nbsp;&amp;nbsp; &lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;的&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;雪”，并会匹配&lt;/span&gt; “&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;我爱冬天&lt;/span&gt;”&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;。&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&amp;quot;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;冬天的雪&lt;/span&gt;“ -&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;》&lt;/span&gt; ”&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;冬天&lt;/span&gt;“ ”&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;的&lt;/span&gt;“ ”&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;雪&lt;/span&gt;“&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;因为“我爱冬天”里有一个“冬天”相匹配。&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;h1&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;六、增量索引建立&lt;/span&gt;&lt;/h1&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;原来有&lt;/span&gt;1000&lt;span style=&quot;font-family:宋体&quot;&gt;万条记录，建立完成索引了，&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;后来又添加了&lt;/span&gt;500&lt;span style=&quot;font-family:宋体&quot;&gt;条记录，&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;思路：把后面添加的记录单独建立索引，建立的增量索引再合并到主索引上面。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;具体实时：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;）新建一张表，用于记录电影最大的&lt;/span&gt;id,&lt;span style=&quot;font-family:宋体&quot;&gt;当主索引建完之后，表里面就记录最大的&lt;/span&gt;id,&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;（&lt;/span&gt;2&lt;span style=&quot;font-family:宋体&quot;&gt;）建立增量索引是主查询，就使用条件了。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;select id,title,description from dede_archives where id&amp;gt;(select max_id from a)&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 'MyISAM引擎中的全文索引是专门对文本创建 索引的，但对中文的支持不好', 'Coreseek 是一款中文全文检索/搜索软件,基于Sphinx研发并独立发布， 专攻中文搜索和信息处理领域，适用于行业/垂直搜索、论坛/站内搜索 数据库搜索、文档/文献检索、信息检索、数据挖掘等应用场景', 'Article/56504639c47a1.png', '20', '127.0.0.1', '1528101433', '1448101572', '48', '0', '1', '278', '1');
INSERT INTO `bk_article` VALUES ('6', '一入IT深似海，程序员的职业生涯', '杨松', '&lt;ul class=&quot;infos list-paddingleft-2&quot; style=&quot;list-style-type: none;&quot;&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;之前谈《&lt;a href=&quot;file:///D:/%E8%B0%B7%E6%AD%8C%E4%B8%8B%E8%BD%BD%E7%9B%AE%E5%BD%95/templates/my_Blog_temp/yangqqqqqqqqqqqqqq/www.yangqq.com/news/life/2014-07-10/722.html&quot; target=&quot;_blank&quot; style=&quot;margin: 0px; padding: 0px; text-decoration: none; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; color: rgb(0, 0, 0);&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;女程序员职业生涯该如何发展？&lt;/strong&gt;&lt;/span&gt;&lt;/a&gt;》其实，女程序员的职业生涯相对于男程序员来说是很短暂的。就目前我来说，从事这行都已有五年时间，当觉得自己在一个瓶颈阶段无法突破自己的时候，出现了html5和css3，新的技术研究，可以重拾当初学习的激情和兴趣，于是又坚持至今....&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;个人博客也是动力之一，博客从一年前的每天访问IP不到60人升到了如今每天将近400个IP，跳出率也由60%缩短到了30%。博客模板也换了n个版本，也结交了很多朋友，一次次的更新和进步还有网友的支持才换来今天的成绩...&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;对于刚毕业的学生来说，想学习建网站，掌握一技之长，最简单的入门无非就是学会html和css，先前发表过一篇文章《&lt;a href=&quot;file:///D:/%E8%B0%B7%E6%AD%8C%E4%B8%8B%E8%BD%BD%E7%9B%AE%E5%BD%95/templates/my_Blog_temp/yangqqqqqqqqqqqqqq/www.yangqq.com/jstt/bj/2013-07-28/530.html&quot; target=&quot;_blank&quot; style=&quot;margin: 0px; padding: 0px; text-decoration: none; color: rgb(51, 51, 51);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; color: rgb(0, 0, 0);&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;如果要学习web前端开发，需要学习什么？&lt;/strong&gt;&lt;/span&gt;&lt;/a&gt;》，如果你还不知道如何着手，请仔细阅读....7天的时间，入门是没有问题的，当然，如果你用心的话！&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; color: rgb(51, 153, 102);&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;即便是坑，我也想要拉你入伙！&lt;/strong&gt;&lt;/span&gt;&lt;span style=&quot;margin: 0px; padding: 0px; color: rgb(255, 102, 0);&quot;&gt;even the pit , and i also want to pull your occupation !&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;前两天有网友跟我聊天，谈到互联网的前景，他很迷茫，要不要继续坚持下去，我说“如果是我，我在这一行肯定干不了多久，毕竟，我的人生规划，就是以后相夫教子。”我劝他，如果自己在这一行这么迷茫堪忧下去，只会耽误自己的时间，趁着年轻，重新找自己喜欢的工作和行业，并且坚持下去....&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;表妹刚刚毕业，之前选专业的时候，就是冲着学html去的，结果几年时间下来，学的是维护...女孩子学这个，确实有点儿让人崩溃，这下又得走我的老路了，还是得自己学习。我不强迫她一定要学习html和css。如果她不感兴趣，是不能勉强的。就像找工作和对象一样，需要不断的摸索，才能找到自己的喜欢的....&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; padding: 0px;&quot;&gt;但是学习一技之长也并不是一件坏事，有很多传统的企业已经开始往互联网方面发展，这方面的人才缺口其实也蛮大，如果说入门需要7天，何不花7天的时间，学习这一技之长，哪怕作为自己的一个后备。即便是坑，我也想要拉你入伙！&lt;/p&gt;&lt;/ul&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '想学习建网站，掌握一技之长，最简单的入门无非就是学会html和css', '女程序员的职业生涯相对于男程序员来说是很短暂的', 'Article/565048cbd5766.jpg', '8', '127.0.0.1', '1533343437', '0', '12', '0', '1', '435', '1');
INSERT INTO `bk_article` VALUES ('8', '简单的html+css企业单页宣传模板', '杨松', '&lt;p&gt;&lt;span style=&quot;color: rgb(117, 111, 113); font-family: 宋体, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 28px;&quot;&gt;分享一个企业的单页宣传模板，介绍企业的主要相关信息，产品信息，以及企业的联系方式等。适合只做宣传的公司，页面简单就行。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: rgb(117, 111, 113); font-family: 宋体, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 28px;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20151121/1448102534887573.jpg&quot; title=&quot;1448102534887573.jpg&quot; alt=&quot;a.jpg&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: rgb(117, 111, 113); font-family: 宋体, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 28px;&quot;&gt;&lt;span style=&quot;color: rgb(117, 111, 113); font-family: 宋体, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 28px;&quot;&gt;再在这个设计理念的基础上，做了一个稍微多样的模板，&lt;/span&gt;&lt;span style=&quot;margin: 0px; padding: 0px; text-decoration: none; color: rgb(51, 51, 51); font-family: 宋体, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 28px; white-space: normal;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px;&quot;&gt;&lt;a href=&quot;http://www.3dst.cn/&quot; target=&quot;_blank&quot; style=&quot;margin: 0px; padding: 0px; text-decoration: none; color: rgb(51, 51, 51); font-family: 宋体, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 28px; white-space: normal;&quot;&gt;演示页面&lt;/a&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: rgb(117, 111, 113); font-family: 宋体, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 28px;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; text-decoration: none; color: rgb(51, 51, 51); font-family: 宋体, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 28px; white-space: normal;&quot;&gt;&lt;span style=&quot;color: rgb(117, 111, 113); font-family: 宋体, Arial, Helvetica, sans-serif; font-